let currentQuestionIndex = 0;

function loadQuestion() {
  const questionElement = document.getElementById("question");
  const optionsElement = document.getElementById("options");
  const feedbackElement = document.getElementById("feedback");
  const translationElement = document.getElementById("translation");

  const currentQuestion = questions[currentQuestionIndex];
  questionElement.textContent = currentQuestion.question;

  optionsElement.innerHTML = ""; // Limpa opções anteriores
  feedbackElement.textContent = ""; // Limpa feedback
  translationElement.textContent = ""; // Limpa tradução

  currentQuestion.options.forEach((option, index) => {
    const button = document.createElement("button");
    button.textContent = option;
    button.onclick = () => checkAnswer(index);
    optionsElement.appendChild(button);
  });

  // Habilita ou desabilita o botão "Voltar"
  document.getElementById("prev-question").disabled = currentQuestionIndex === 0;
}

function checkAnswer(selectedIndex) {
  const currentQuestion = questions[currentQuestionIndex];
  const feedbackElement = document.getElementById("feedback");
  const translationElement = document.getElementById("translation");
  const optionsButtons = document.querySelectorAll("#options button");

  optionsButtons.forEach((button, index) => {
    button.disabled = true;
    if (index === currentQuestion.answer) {
      button.classList.add("correct");
    } else if (index === selectedIndex) {
      button.classList.add("incorrect");
    }
  });

  const isCorrect = selectedIndex === currentQuestion.answer;
  feedbackElement.textContent = isCorrect ? "Parabéns, você acertou!" : "Resposta incorreta";
  feedbackElement.style.color = isCorrect ? "#4CAF50" : "#f44336";

  // Exibe a tradução se a resposta estiver correta
  if (isCorrect) {
    translationElement.textContent = `Tradução: ${currentQuestion.translation}`;
  }
}

function nextQuestion() {
  currentQuestionIndex++;
  if (currentQuestionIndex < questions.length) {
    loadQuestion();
  } else {
    document.getElementById("quiz-container").innerHTML = "<h2>Quiz Completo!</h2>";
  }
}

function previousQuestion() {
  if (currentQuestionIndex > 0) {
    currentQuestionIndex--;
    loadQuestion();
  }
}

function restartQuiz() {
  const confirmRestart = confirm("Tem certeza de que deseja reiniciar o quiz?");
  if (confirmRestart) {
    currentQuestionIndex = 0;
    document.getElementById("quiz-container").innerHTML = `
      <div id="question"></div>
      <div id="options"></div>
      <button id="prev-question" onclick="previousQuestion()">Voltar</button>
      <button id="next-question" onclick="nextQuestion()">Próxima</button>
      <button id="restart" onclick="restartQuiz()">Reiniciar Quiz</button>
      <p id="feedback"></p>
      <p id="translation"></p>
    `;
    loadQuestion();
  }
}

window.onload = loadQuestion;

// Variáveis para armazenar os contadores de acertos e erros
let correctCount = 0;
let incorrectCount = 0;

// Função para atualizar os contadores na página
function updateScore() {
  document.getElementById("correct-count").textContent = correctCount;
  document.getElementById("incorrect-count").textContent = incorrectCount;
}

// Modifique a função checkAnswer para atualizar os contadores
function checkAnswer(selectedIndex) {
  const currentQuestion = questions[currentQuestionIndex];
  const feedbackElement = document.getElementById("feedback");
  const translationElement = document.getElementById("translation");
  const optionsButtons = document.querySelectorAll("#options button");

  optionsButtons.forEach((button, index) => {
    button.disabled = true;
    if (index === currentQuestion.answer) {
      button.classList.add("correct");
    } else if (index === selectedIndex) {
      button.classList.add("incorrect");
    }
  });

  const isCorrect = selectedIndex === currentQuestion.answer;
  feedbackElement.textContent = isCorrect ? "Parabéns, você acertou!" : "Resposta incorreta";
  feedbackElement.style.color = isCorrect ? "#4CAF50" : "#f44336";

  // Atualiza contadores de acertos e erros
  if (isCorrect) {
    correctCount++;
  } else {
    incorrectCount++;
  }

  // Atualiza os valores exibidos na página
  updateScore();

  // Exibe a tradução se a resposta estiver correta
  if (isCorrect) {
    translationElement.textContent = `Tradução: ${currentQuestion.translation}`;
  }
}

